var COOKIE_NAME = 'cojones-cookie';

$(document).click(function(){
		$('.title-banner').fadeOut(500);
		$('.blur-overlay').fadeOut(500);
});

$(document).ready(function(){

	$('.mobile-menu-container').headroom();

    $(window).scroll(function(){
        $('.title-banner').fadeOut(500);
        $('.blur-overlay').fadeOut(500);
    })

    $('.hamburger').click(function(){
		$(this).toggleClass('is-active');
		$('.mobile-menu-items').toggleClass('is-active');
	});	


	$('#main').masonry({
	  // options
	  itemSelector: '.post',
	 // columnWidth: 200
	});

	
})


	